    splitHTML(texto) {
      let regex = /(<([^>]+)>)/gi;
      let textoS = texto.replace(regex, "");
      let maxLength = 65;
      let trimmedString = textoS.substr(0, maxLength);
      trimmedString = trimmedString.substr(0, Math.min(trimmedString.length, trimmedString.lastIndexOf(" ")));
      return trimmedString + " ...";
